<?php 	$root = root();?><section id="content" class="content-container"><section class="page-form-ele page">  <section class="panel panel-default">        <div class="panel-heading"><strong><span class="glyphicon glyphicon-th"></span> Go To</strong></div>        <div class="panel-body">            <div class="space"></div>            <a class="file-input-wrapper btn btn-default  btn-success" href="<?= base_url('Home') ?>"> Dashboard</a>        </div>    </section><div class="row">         <div class="col-lg-12">            <!-- Radio buttons and checkbox -->            <section class="panel panel-default">                <div class="panel-heading"><strong>				<span class="glyphicon glyphicon-th"></span> Change Password</strong></div>                <div class="panel-body">					<div class="errorsinci">				</div>					  <span id="responsesMsg"></span>										<?php echo form_open('Login/Passwordchangeend',array('class'=>'ng-pristine ng-valid','name'=>'change_password','id'=>'change_password')); ?>                      <div class="form-group">                            <label for="exampleInputEmail1">Old Password</label>							<input type="password"  placeholder="Enter Old Password" class="form-control" id="myInput" name="o_pass" />              <input type="checkbox" onclick="myFunction()">Show Password						</div>						 <div class="form-group">							<label for="exampleInputEmail1">New Password</label>							<input type="password"  placeholder="Enter New Password" class="form-control"  name="n_pass" id="n_pass" pattern=".{5,10}" required title="5 to 10 characters"/>              <input type="checkbox" onclick="myFunction1()">Show Password					</div>						<div class="form-group">                            <label for="exampleInputEmail1">Confirm Password</label>                            <input class="form-control"    placeholder="Enter Confirm Password" type="password" name="c_pass" id="c_pass" pattern=".{5,10}" required title="5 to 10 characters">                            <input type="checkbox" onclick="myFunction2()">Show Password                        </div>                        <button type="submit" class="btn btn-primary btn-w-md">Submit</button>                  <?php echo form_close(); ?>                </div>            </section>            <!-- end Radio buttons and checkbox -->                    </div>    </div></section></section> <script>// change username form$(function() {  $("form[name='change_password']").validate({    // Specify validation rules    rules: {      o_pass: "required",        n_pass: "required",   		c_pass: {            required: true,             equalTo: "#n_pass"        }	       },    // Specify validation error messages    messages: {      o_pass: "Enter Old Password",       n_pass: "Enter New Password", 	   c_pass: "Your Passwords Must Match"     },     submitHandler: function(form) {		$("#payroll_loader").css("display", "block");		$('#responsesMsg').html('');		var url = "<?php echo root(); ?>";		$.ajax({			url: url+"Login/Passwordchangeend",			type: 'post',			dataType: 'json',			data: $('form#change_password').serialize(),			success: function(data) { 				if(data.status==1){					$('#responsesMsg').html('<div class="callout callout-info"><p class="ng-binding"><i class="fa fa-check-circle"></i> '+data.msg+'</p></div>').show().fadeOut(5000); 					$('#n_pass').data('');					$('#c_pass').data('');					$("#payroll_loader").css("display", "none");				}else{					$('#responsesMsg').html('<div class="callout callout-warning"><p class="ng-binding"><i class="fa fa-times-circle-o"></i> '+data.msg+'</p></div>').show().fadeOut(5000); 					$("#payroll_loader").css("display", "none");				}					  }		});		 return false;    }  });});</script><script>function myFunction() {  var x = document.getElementById("myInput");  if (x.type === "password") {    x.type = "text";  } else {    x.type = "password";  }}</script><script>function myFunction1() {  var x = document.getElementById("n_pass");  if (x.type === "password") {    x.type = "text";  } else {    x.type = "password";  }}</script><script>function myFunction2() {  var x = document.getElementById("c_pass");  if (x.type === "password") {    x.type = "text";  } else {    x.type = "password";  }}</script>