<?php  $root = root(); ?><style> 	</style> <section id="content" class="content-container"><section class="page-form-ele page">  <section class="panel panel-default">        <div class="panel-heading"><strong><span class="glyphicon glyphicon-th"></span> Go To</strong></div>        <div class="panel-body">				<div class="space"></div>           <a class="file-input-wrapper btn btn-default  btn-success" href="<?= base_url('Home') ?>"> Dashboard</a>        </div>    </section><div class="row">         <div class="col-lg-12">            <!-- Radio buttons and checkbox -->            <section class="panel panel-default">                <div class="panel-heading"><strong>				<span class="glyphicon glyphicon-th"></span> Change Username</strong></div>                <div class="panel-body">					<span id="responsesMsg"></span>				   <?php echo form_open('admin/Login/usernamechangeEnd',array('class'=>'ng-pristine ng-valid','name'=>'change_username','id'=>'change_username')); ?>					<div class="form-group">							<label class="" for="exampleInputEmail1">Change Username</label>                            <input type="box"  value="<?php foreach(SelectQuery('username','users','id','1') as $raa) echo $raa->username; ?>" placeholder="Please enter username" class="form-control" name="username" required />                    </div>					<button type="submit" class="btn btn-primary btn-w-md">Submit</button>				</div>		</section>		<!-- end Radio buttons and checkbox -->            	</div>    </div> </section> </section> <script>// $(function() {  $("form[name='change_username']").validate({    // Specify validation rules    rules: {      username: "required",       },    // Specify validation error messages    messages: {      username: "Enter Username",      },     submitHandler: function(form) {		$("#payroll_loader").css("display", "block");		$('#responsesMsg').html('');		var url = "<?php echo root(); ?>";		$.ajax({			url: url+"Login/usernamechangeEnd",			type: 'post',			dataType: 'json',			data: $('form#change_username').serialize(),			success: function(data) { 				if(data.status==1){					$('#responsesMsg').html('<div class="callout callout-info"><p class="ng-binding"><i class="fa fa-check-circle"></i> User Name Change Successfully</p></div>').show().fadeOut(500000000); 					$('#admin_name').html(data.admin_name);					$("#payroll_loader").css("display", "none");				}else{					$('#responsesMsg').html('<div class="callout callout-warning"><p class="ng-binding"><i class="fa fa-times-circle-o"></i> Something Wrong</p></div>').show().fadeOut(5000); 					$("#payroll_loader").css("display", "none");				}					  }		});		 return false;    }  });});</script>